<template>
	<view class="container">
		<!-- 头部轮播 -->
		<view class="carousel-section">
			<!-- 标题栏和状态栏占位符 -->
			<view class="titleNview-placing"></view>
			<!-- 背景色区域 -->
			<view class="titleNview-background" :style="{backgroundColor:titleNViewBackground}"></view>
			<swiper class="carousel" circular @change="swiperChange">
				<swiper-item
					v-for="(item, index) in carouselList"
					:key="index" class="carousel-item"
					@click="navToDetailPage({title: '轮播广告'})"
				>
					<image :src="item.src" />
				</swiper-item>
			</swiper>
			<!-- 自定义swiper指示器 -->
			<view class="swiper-dots">
				<text class="num">{{swiperCurrent+1}}</text>
				<text class="sign">/</text>
				<text class="num">{{swiperLength}}</text>
			</view>
		</view>
		<!-- 任务列表 -->
		<view v-if="taskList.length">
			<item-card
				v-for="(item, index) in taskList"
				:key="index"
				v-bind="{...item}"
				@click="toTaskTestPage(item)"
			></item-card>
		</view>
		<!-- 缺省图 -->
		<empty-box top="300" v-else></empty-box>
	</view>
</template>

<script>
	import { ItemCard, EmptyBox } from 'wh-ui'
	export default {
		components:{ ItemCard, EmptyBox },
		data() {
			return {
				titleNViewBackground: '',
				swiperCurrent: 0,
				swiperLength: 0,
				carouselList: [],
				taskList: [
					{title: '测试3', subTitle: '西安测试1', desc: '4323', device: '开关'},
					{title: '测试1', subTitle: '西安测试1', desc: '4323', device: '开关'},
					{title: '测试1', subTitle: '西安测试1', desc: '4323', device: '开关'},
					{title: '测试1', subTitle: '西安测试1', desc: '4323', device: '开关'},
					{title: '测试1', subTitle: '西安测试1', desc: '4323', device: '开关'},
					{title: '测试2', subTitle: '西安测试2', desc: '11242342321212', device: '开关'},
					{title: '测试3', subTitle: '西安测试3', desc: '112发到付顺丰21212', device: '开关'}
				]
			};
		},

		onLoad() {
			this.loadData();
			this.getTaskList()
		},
		methods: {
			/**
			 * 获取任务列表
			 */
			getTaskList() {
				this.$Service.getTaskList({tester: '6046ea094d554e548ac780012b3ae139'}).then((resp) => {
					if (!resp || !resp.success) return;
					console.log('resp.data:::', resp.data)
					this.taskList = resp.data
				})
			},
			/**
			 * 转到task测试页面
			 */
			toTaskTestPage(item) {
				uni.navigateTo({
					url: `/pages/Task/InfTemp?${this.$stringify(item)}`
				})
			},
			/**
			 * 请求静态数据只是为了代码不那么乱
			 * 分次请求未作整合
			 */
			async loadData() {
				let carouselList = await this.$api.json('carouselList');
				this.titleNViewBackground = carouselList[0].background;
				this.swiperLength = carouselList.length;
				this.carouselList = carouselList;
			},
			//轮播图切换修改背景色
			swiperChange(e) {
				const index = e.detail.current;
				this.swiperCurrent = index;
				this.titleNViewBackground = this.carouselList[index].background;
			},
			//详情页
			navToDetailPage(item) {
				//测试数据没有写id，用title代替
				let id = item.title;
				uni.navigateTo({
					url: `/pages/product/product?id=${id}`
				})
			},
		},
		//点击导航栏 buttons 时触发
		onNavigationBarButtonTap() {
			uni.navigateTo({
				url: `/pages/Set/index`
			})
		}
	}
</script>

